    /// <summary>
    /// A normal-distribution-like function for breathing light band
    /// </summary>
    /// <param name="x">The X as input</param>
    /// <param name="w">The width of the curve</param>
    /// <returns>The Y value from the input</returns>
    public static float Distribution(float x, float w)
    {
        float input = x / w;
        float Y = MathF.Exp(-MathF.Pow(input, 2));
        return Y;
    }

